mapper.update:
update t_board set
	b_title = #{b_title},
	b_content = #{b_content}
where b_num = #{b_num}

update t_board set
	b_title = #{b_title},
	b_content = #{b_content},
	b_pic = #{b_pic}
where b_num = #{b_num}

update t_board set
	b_read_count = b_read_count + 1
where b_num = #{b_num}

update t_board set 
	b_step = b_step + 1 
where b_ref = #{b_ref} and b_step > #{b_step}
